Method.Implementation:
if (data.id '= ""){ set company = ..%OpenId(data.id,3,.status) set company.FullName.Value = $ZCONVERT(data.fullName, "I","UTF8") set company.ShortName.Value = $ZCONVERT(data.shortName, "I","UTF8") set company.LegalAddress.Value = $ZCONVERT(data.legalAddress, "I","UTF8") set company.MailAddress.Value = $ZCONVERT(data.mailAddress, "I","UTF8") set company.OtherInfo.Value = $ZCONVERT(data.otherInfo, "I","UTF8") } else{ set company = ..%New() set company.FullName = ##class(Stc.Data.Localization.LText).Create($ZCONVERT(data.fullName, "I","UTF8")) set company.ShortName = ##class(Stc.Data.Localization.LText).Create($ZCONVERT(data.shortName, "I","UTF8")) set company.LegalAddress = ##class(Stc.Data.Localization.LText).Create($ZCONVERT(data.legalAddress, "I","UTF8")) set company.MailAddress = ##class(Stc.Data.Localization.LText).Create($ZCONVERT(data.mailAddress, "I","UTF8")) set company.OtherInfo = ##class(Stc.Data.Localization.LText).Create($ZCONVERT(data.otherInfo, "I","UTF8")) }
set company.BIC = $ZCONVERT(data.bic, "I","UTF8") set company.OGRN = $ZCONVERT(data.ogrn, "I","UTF8") set company.INN = $ZCONVERT(data.inn, "I","UTF8") set company.SettlementAccount = $ZCONVERT(data.settlementAccount, "I","UTF8") set company.CorrespondentAccount = $ZCONVERT(data.correspondentAccount, "I","UTF8") set company.KPP = $ZCONVERT(data.kpp, "I","UTF8") set company.WebSite = $REPLACE($REPLACE($ZCONVERT(data.site, "I","UTF8"),"http://",""), "https://","")
set status = company.%Save() quit company

set company = ..%OpenId(id,4,.status)
quit:$$$ISERR(status) ##class(Stc.Utils.Msg).GetErrorStatus("openCompany", "| "_status)

set findId = ""

// Проверка: есть ли в заявках	
&sql(SELECT ID INTO:findId FROM Stc_Data.Order WHERE Company = :id)
if (findId = ""){
	// Проверка: есть ли сотрудники
	&sql(SELECT ID INTO:findId FROM Stc_Data.Person WHERE Company = :id)

	if (findId = ""){
		// Проверка: плательщик подгруппы
		&sql(SELECT ID INTO:findId FROM Stc_Data.SubGroup WHERE Payer = :id)	
	}
}

// Выход, если есть связи
quit:findId'="" ##class(Stc.Utils.Msg).GetErrorStatus("deleteCompanyImpossible")

// Удаление локализации
set status = ##class(Stc.Data.Localization.LText).Delete(company.FullName.%Id())
quit:$$$ISERR(status) status

set status = ##class(Stc.Data.Localization.LText).Delete(company.ShortName.%Id())
quit:$$$ISERR(status) status

set status = ##class(Stc.Data.Localization.LText).Delete(company.LegalAddress.%Id())
quit:$$$ISERR(status) status

set status = ##class(Stc.Data.Localization.LText).Delete(company.MailAddress.%Id())
quit:$$$ISERR(status) status

set status = ##class(Stc.Data.Localization.LText).Delete(company.OtherInfo.%Id())
quit:$$$ISERR(status) status

// Удаление компании
set status = ..%DeleteId(id, 4)
quit:$$$ISERR(status) ##class(Stc.Utils.Msg).GetErrorStatus("deleteCompany", "| "_status)

quit $$$OK
