Method.Implementation:
#dim group As MailingGroup = ""

if (data.id = ""){
	set group = ..%New()
	set group.AccessCode = $SYSTEM.Util.CreateGUID()
}
else{
	set group = ..%OpenId(data.id)
}

set group.Name = $ZCVT(data.name, "I", "UTF8")
set group.Description = $ZCVT(data.description, "I", "UTF8")

set st = group.%Save()

quit group

if (..AccessCodeIdxExists("Announcement")){ quit ..AccessCodeIdxOpen("Announcement") }
set gr = ..%New() set gr.Name = "Подписка на анонсы" set gr.Description = "Подписка на анонсы" set gr.AccessCode = "Announcement" do gr.%Save()
quit gr
