case 'HYBRID':
	mapType = google.maps.MapTypeId.HYBRID;
	break;
case 'SATELLITE':
	mapType = google.maps.MapTypeId.SATELLITE;
	break;
case 'TERRAIN':
	mapType = google.maps.MapTypeId.TERRAIN;
	break;
case 'ROADMAP':
default:
	break;
}

return mapType;

