switch(property) {
case 'latitude':
case 'longitude':
	this[property] = value;
	if (map) {
		var mapLatlng = new google.maps.LatLng(parseFloat(this.latitude), parseFloat(this.longitude));
		map.panTo(mapLatlng);
	}
	break;

case 'zoom':
	this[property] = value;
	if (map) {
		map.setZoom(parseInt(this.zoom,10));
	}
	break;

case 'mapType':
	this[property] = value;
	if (map) {
		var mapTypeId = this.getMapTypeId(this.mapType);
		map.setMapTypeId(mapTypeId);
	}
	break;

default:
	return this.invokeSuper('setProperty',arguments);
}
return true;

switch(reason) {
case 'dataChange':
	this.onApplyFilters(true);
case 'modelChange':
	// some change in dataController
	this.acquireData();
	this.syncMarkers();
	this.syncPolygons();
	break;
case 'seriesChange':
	break;
