Method.Implementation:
set %response.ContentType = ..#ContentType set %response.CharSet = ..#CharSet
set status = $$$OK
try{ set training = ##class(Stc.Data.Training).%OpenId(trainingId) $$$THROWONERROR(status, status)
set course = ##class(Stc.Data.Localization.LTextValue).GetValue(training.Course.Name.%Id(), lang) set city = ##class(Stc.Data.Localization.LTextValue).GetValue(training.City.Name.%Id(), lang) set date = $ZDATE($NOW(),3)
set fileName = ##class(%MessageDictionary).FormatText($G(^CacheMsg("Stc", lang, "certificatesReport"),"---"), course, city, date)_".csv"
if ($FIND(%request.UserAgent, "Firefox") '= 0) { set fileName = $REPLACE(fileName, " ", "_") }
do %response.SetHeader("Content-Disposition","attachment;filename="_fileName)
&sql(DECLARE CertCur CURSOR FOR SELECT Number, Student->LastName, Student->FirstName, Student->MiddleName FROM Stc_Data.Certificate WHERE Training = :trainingId)
&sql(OPEN CertCur) for { &sql(FETCH CertCur INTO :number, :lnameId, :fnameId, :mnameId) quit:(SQLCODE '= 0)
set lname = ##class(Stc.Data.Localization.LTextValue).GetValue(lnameId, lang) set fname = ##class(Stc.Data.Localization.LTextValue).GetValue(fnameId, lang) set mname = ##class(Stc.Data.Localization.LTextValue).GetValue(mnameId, lang)
w number_";"_lname_";"_fname_";"_mname,! }
&sql(CLOSE CertCur) } catch ex{ set status = ex.AsStatus() }
q status
