Export.CSP:
'use strict';
//sddddd

directivesModule.directive('stcschedule', function(){
    return {
        replace: true,
        restrict: 'E',
        templateUrl: 'components/stcschedule.csp',

        scope: {
            course: '='
        },
        controller: function($scope, $window){           
            $scope.openProgram = function(url){
                $window.open('http://' + url, '_blank');
            };

            $scope.createOrder = function(courseId){
                var host = window.location.host;
                $window.open('http://' + host + StcAppSetting.default + '/stc/index.csp#/createorder?course=' + courseId, '_blank');
            };

            $scope.join = function(trainingId){
                var host = window.location.host;
                $window.open('http://' + host + StcAppSetting.default + '/stc/index.csp#/orderstudent?training=' + trainingId, '_blank');
            };  
	   	}
    }
});
