/*===========================================================================================
Для работы с яндексом
===========================================================================================*/

servicesModule.factory('YandexSrvc', function(DALSrvc) {
	return {
		// Получить координаты адреса
        getResult: function(string){
	       return DALSrvc.getPromise('get', 'http://geocode-maps.yandex.ru/1.x/?Lang=' +  StcAppSetting.lang + '&sco=latlong&format=json&geocode=' + string, null);
        }
    }
});
